mapper.select:
SELECT * FROM devices

    AND lab_id = #{labId}
    AND status = #{status}

        AND (name LIKE CONCAT('%', #{keyword}, '%') OR model LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY id DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(1) FROM devices

    AND lab_id = #{labId}
    AND status = #{status}

        AND (name LIKE CONCAT('%', #{keyword}, '%') OR model LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE devices
SET lab_id = #{labId},
    name = #{name},
    model = #{model}
WHERE id = #{id}
