mapper.select:
SELECT * FROM reservations

    AND is_deleted = 0
    AND lab_id = #{labId}
    AND device_id = #{deviceId}
    AND requester_id = #{requesterId}

        AND status IN

            #{item}


    AND type = #{type}
    AND start_time >= #{fromTime}
    AND end_time <= #{toTime}

ORDER BY start_time DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(1) FROM reservations

    AND is_deleted = 0
    AND lab_id = #{labId}
    AND device_id = #{deviceId}
    AND requester_id = #{requesterId}

        AND status IN

            #{item}


    AND type = #{type}
    AND start_time >= #{fromTime}
    AND end_time <= #{toTime}

mapper.update:
UPDATE reservations
SET start_time = #{startTime},
    end_time = #{endTime}
WHERE id = #{id}

UPDATE reservations
SET status = #{status},
    approved_by = #{approvedBy},
    approved_at = #{approvedAt},
    cancel_reason = #{cancelReason}
WHERE id = #{id}

mapper.select:
SELECT * FROM reservations
WHERE ((lab_id = #{labId}) OR (device_id = #{deviceId}))
  AND is_deleted = 0
  AND start_time < #{endTime}
  AND end_time > #{startTime}
  AND id != #{excludeId}

SELECT * FROM reservations
WHERE start_time <= #{toTime} AND end_time >= #{fromTime}
AND is_deleted = 0
AND lab_id = #{labId}
AND device_id = #{deviceId}
ORDER BY start_time ASC
